                                "local_loss": {
                                    "target": "Shallow projections (early layers)",
                                    "masking": "Unstructured (random patches, like MAE).",
                                    "goal": "Preserve *fine-grained details* (e.g., ‘this pixel cluster is a boat wake’)."
                                }
                            }
